import { Component } from "react";
import {
  Button,
  Input,
  Form,
  FormGroup,
  Label,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";
import TodoItems from "./todoItemsComponant";

class Detailed extends Component {
  constructor(props) {
    super(props);
    this.state = {
      setModal: false,

      items: [],
    };
    this.addItem = this.addItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }
  openModal = () => {
    this.setState({
      showModal: !this.state.showModal,
    });
  };
  addItem(e) {
    if (this._inputElement.value !== "") {
      var newItem = {
        text: this._inputElement.value,
        key: Date.now(),
      };
      this.setState((prevState) => {
        return {
          items: prevState.items.concat(newItem),
        };
      });

      this._inputElement.value = "";
    }

    console.log(this.state.items);

    e.preventDefault();
  }
  deleteItem(key) {
    var filteredItems = this.state.items.filter(function (item) {
      return item.key !== key;
    });

    this.setState({
      items: filteredItems,
    });
  }

  render() {
    return (
      <div className="container">
        <div className="todoListMain">
          <div className="header">
            <h1>Detailed To Do List</h1>

            <Button onClick={this.openModal}>Create Item</Button>
          </div>
          <TodoItems entries={this.state.items} delete={this.deleteItem} />
        </div>

        <Modal isOpen={this.state.showModal} toggle={this.state.showModal}>
          <ModalHeader toggle={this.openModal}>Modal title</ModalHeader>
          <ModalBody>
            <Input
              placeholder="Write something (data should remain in modal if unmountOnClose is set to false)"
              rows={5}
              type="textarea"
            />
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.openModal}>
              Do Something
            </Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}
export default Detailed;
