import { Component } from "react";
import {
  Button,
  Input,
  Form,
  FormGroup,
  Label,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";

class Detailed extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
    };
    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
  }
  handleOpenModal = () => {
    this.setState({
      showModal: true,
    });
  };
  handleCloseModal = () => {
    this.setState({
      showModal: false,
    });
  };
  render() {
    return (
      <div className="container">
        <div className="todoListMain">
          <div className="header">
            <h1>Detailed To Do List</h1>

            <Button onClick={this.openModal}>Create Item</Button>
          </div>
        </div>

        <Modal isOpen={this.state.showModal} toggle={this.state.showModal}>
          <ModalHeader toggle={this.handleCloseModal}>Modal title</ModalHeader>
          <ModalBody>
            <Input
              placeholder="Write something (data should remain in modal if unmountOnClose is set to false)"
              rows={5}
              type="textarea"
            />
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
export default Detailed;
