import { Component } from "react";
import {
  Button,
  Input,
  Form,
  FormGroup,
  Label,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";
import TodoItems from "./todoItemsComponant";

class Detailed extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
    };
    this.addItem = this.addItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }
  addItem(e) {
    if (this._inputElement.value !== "") {
      var newItem = {
        text: this._inputElement.value,
        key: Date.now(),
      };
      this.setState((prevState) => {
        return {
          items: prevState.items.concat(newItem),
        };
      });

      this._inputElement.value = "";
    }

    console.log(this.state.items);

    e.preventDefault();
  }
  deleteItem(key) {
    var filteredItems = this.state.items.filter(function (item) {
      return item.key !== key;
    });

    this.setState({
      items: filteredItems,
    });
  }

  render() {
    return (
      <div className="container">
        <div className="todoListMain">
          <div className="header">
            <h1>Detailed To Do List</h1>

            <Button type="submit">Create Item</Button>
          </div>
          <TodoItems entries={this.state.items} delete={this.deleteItem} />
        </div>
        <Form inline onSubmit={function noRefCheck() {}}>
          <FormGroup>
            <Label for="unmountOnClose">UnmountOnClose value</Label>{" "}
            <Input
              id="unmountOnClose"
              name="unmountOnClose"
              onChange={function noRefCheck() {}}
              type="select"
            >
              <option value="true">true</option>
              <option value="false">false</option>
            </Input>
          </FormGroup>{" "}
          <Button color="danger" onClick={function noRefCheck() {}}>
            Click Me
          </Button>
        </Form>
        <Modal toggle={function noRefCheck() {}}>
          <ModalHeader toggle={function noRefCheck() {}}>
            Modal title
          </ModalHeader>
          <ModalBody>
            <Input
              placeholder="Write something (data should remain in modal if unmountOnClose is set to false)"
              rows={5}
              type="textarea"
            />
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={function noRefCheck() {}}>
              Do Something
            </Button>{" "}
            <Button onClick={function noRefCheck() {}}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}
export default Detailed;
